mapper.select:
SELECT authority
FROM   com_authority
WHERE  url = #{url}

SELECT lgn_fail_cnt
FROM   com_user
WHERE  user_id = #{userId}

mapper.update:
UPDATE com_user
SET enabled = 0
WHERE user_id = #{userId}

UPDATE com_user
SET lgn_fail_cnt = 0
WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM  com_user
WHERE user_id = #{userId}
